%window_defaults=(
		'x'	=> 0,
		'y'	=> 0,
		'width'	=> 500,
		'height'=> 500,
		'parent'=> 0,
		'mask'	=> StructureNotifyMask,
		'attributes'=> [GLX_RGBA],
	);
sub glpOpenWindow {
	# default values
	my(%a) = @_;
	my(%p) = %window_defaults;
	foreach $k (keys(%a)){
		defined($p{$k}) || warn "Not a valid parameter to glpOpenWindow: `$k'\n";
		#print "parameter $k now ",$a{$k}," was ",$p{$k},"\n";  
		$p{$k} = $a{$k};
	}
	glpcOpenWindow($p{x},$p{y},$p{width},$p{height},
	               $p{parent},$p{'mask'},
	               @{$p{attributes}});
}


